import React, { useState } from 'react';
import TopSection from './components/TopSection';
import SongInfo from './components/SongInfo';
import Lyrics from './components/Lyrics';
import { SongData, SongState } from './types/song';

const App: React.FC = () => {
  const [songState, setSongState] = useState<SongState>({
    isIdentifying: false,
    songData: null,
    error: null
  });

  const [isListening, setIsListening] = useState(false);

  const handleSongRecognized = (songData: SongData) => {
    setSongState({
      isIdentifying: false,
      songData,
      error: null
    });
  };

  return (
    <div className="min-h-screen bg-app-bg text-white p-8">
      <div className="max-w-3xl mx-auto space-y-8">
        <TopSection
          songData={songState.songData}
          isListening={isListening}
          onListeningChange={setIsListening}
          onSongRecognized={handleSongRecognized}
        />

        {songState.songData && (
          <div className="space-y-8 animate-fade-in">
            <SongInfo songData={songState.songData} />
            <Lyrics songData={songState.songData} />
          </div>
        )}

        {songState.error && (
          <div className="text-red-400 text-center">
            {songState.error}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
