import React, { useEffect, useState } from 'react';
import SongInfo from './components/SongInfo';
import Lyrics from './components/Lyrics';
import { Song } from './types/song';

const App: React.FC = () => {
  const [song, setSong] = useState<Song | null>(null);
  const [lyrics, setLyrics] = useState<string[]>([]);

  useEffect(() => {
    // Simulating the original setTimeout for song identification
    const timer = setTimeout(() => {
      setSong({
        title: "Nights on Broadway",
        artist: "Bee Gees",
        album: "Main Course"
      });
      updateLyrics();
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  const updateLyrics = () => {
    const lyrics = [
      "Here we are, in a room full of strangers",
      "Standing in the dark, where your eyes couldn't see me",
      "Well, I had to follow you",
      "Though you did not want me to",
      "That won't stop my loving you"
    ];
    setLyrics(lyrics);
  };

  return (
    <div className="min-h-screen bg-[#1f1f1f] p-5">
      <div className="container max-w-[800px] mx-auto">
        <SongInfo song={song} />
        <Lyrics lyrics={lyrics} />
      </div>
    </div>
  );
};

export default App;
