import React from 'react';

interface LyricsProps {
  lyrics: string[];
}

const Lyrics: React.FC<LyricsProps> = ({ lyrics }) => {
  return (
    <div className="lyrics-container backdrop-blur-lg bg-black/20 rounded-lg p-5 max-h-[60vh] overflow-y-auto">
      <div className="lyrics">
        {lyrics.map((line, index) => (
          <p key={index} className="text-white/60 my-2">{line}</p>
        ))}
      </div>
    </div>
  );
};

export default Lyrics;
