import React, { useState, useEffect } from 'react';
import { useAudioRecorder } from '../hooks/useAudioRecorder';
import { SongData } from '../types/song';

interface AudioRecorderProps {
  onSongRecognized: (songInfo: SongData) => void;
  className?: string;
}

const AudioRecorder: React.FC<AudioRecorderProps> = ({ 
  onSongRecognized,
  className = ''
}) => {
  const { startRecording, isRecording, audioBlob, reset } = useAudioRecorder();
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!isRecording && audioBlob) {
      handleRecognition();
    }
  }, [isRecording, audioBlob]);

  const handleRecognition = async () => {
    if (!audioBlob) return;
    setIsProcessing(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', audioBlob);

    try {
      const response = await fetch('http://localhost:8000/api/identify', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to identify song');
      }

      const result = await response.json();
      onSongRecognized(result);
    } catch (error) {
      console.error('Error during recognition:', error);
      setError(error instanceof Error ? error.message : 'Failed to identify song');
    } finally {
      setIsProcessing(false);
      reset();
    }
  };

  return (
    <div className={`text-center ${className}`}>
      <button
        onClick={() => !isRecording && !isProcessing && startRecording()}
        className="button-85"
        disabled={isRecording || isProcessing}
        role="button"
      >
        {isRecording || isProcessing ? 'Analyzing...' : 'ID Song ðŸŽ§'}
      </button>
      {error && (
        <div className="mt-2 text-red-400 text-sm">
          {error}
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
