import React, { useState, useEffect, useRef } from 'react';
import { useAudioRecorder } from '../hooks/useAudioRecorder';
import { SongData } from '../types/song';

interface AudioRecorderProps {
  onSongRecognized: (songInfo: SongData) => void;
  isListening: boolean;
  onListeningChange: (isListening: boolean) => void;
}

const AudioRecorder: React.FC<AudioRecorderProps> = ({
  onSongRecognized,
  isListening,
  onListeningChange
}) => {
  const { startRecording, stopRecording } = useAudioRecorder();

  const handleToggleRecording = async () => {
    if (!isListening) {
      await startRecording();
      onListeningChange(true);
    } else {
      await stopRecording();
      onListeningChange(false);

      // Mock song recognition result
      const mockSongData: SongData = {
        title: "Nettie",
        artist: "Type O Negative",
        album: "Life Is Killing Me",
        albumArt: "/album-art.jpg",
        lyrics: [
          "Nettie, no need to cry",
          "Let me wipe those tear drops from your eyes",
          "Nettie, no need to cry",
          "Let me wipe those tear drops from your eyes",
          "In the dark bathed in Cathode ray blue",
          "Miss Red Hook of 1922",
          "Weeping silently for the pain of others",
          "Every night a tearful rosary",
          "A victim of the curse of empathy"
        ]
      };
      onSongRecognized(mockSongData);
    }
  };

  return (
    <button
      onClick={handleToggleRecording}
      className={`
        relative px-6 py-2 text-white font-semibold
        rounded-lg bg-gradient-to-r from-green-400 to-blue-400
        transform hover:scale-105 transition-all duration-300
        hover:shadow-lg hover:shadow-green-400/50
        active:scale-95
        ${isListening ? 'animate-pulse' : 'animate-glow'}
      `}
    >
      <span className="relative z-10">
        ID Song {isListening ? '...' : ''}
      </span>
      <div className="absolute inset-0 bg-gradient-to-r from-green-400 to-blue-400 opacity-75 blur-lg rounded-lg" />
    </button>
  );
};

export default AudioRecorder;
