import React, { useState } from 'react';
import AudioRecorder from './components/AudioRecorder';
import { TopSection } from './components/TopSection';
import { Lyrics } from './components/Lyrics';
import { SongData } from './types/song';
import './styles/App.css';

const App: React.FC = () => {
  const [songData, setSongData] = useState<SongData | null>(null);

  const handleSongRecognized = (result: SongData) => {
    setSongData(result);
  };

  return (
    <div className="container">
      {/* Background with album art */}
      {songData?.album_art && (
        <div 
          className="background-art"
          style={{ backgroundImage: `url(${songData.album_art})` }}
        />
      )}

      <div className="content-container">
        {/* Static Top Section */}
        <div className="static-header">
          <AudioRecorder onSongRecognized={handleSongRecognized} />
          <TopSection songData={songData} />
        </div>

        {/* Scrollable Lyrics Section */}
        <div className="lyrics-scroll-container">
          {songData && <Lyrics songData={songData} />}
        </div>
      </div>
    </div>
  );
};

export default App;
