import React, { useState } from 'react';

interface RainbowButtonProps {
  onClick: () => void;
  isListening: boolean;
}

export const RainbowButton: React.FC<RainbowButtonProps> = ({ onClick, isListening }) => {
  return (
    <div className="relative inline-block">
      {/* Background glow effect */}
      <div
        className={`absolute top-0 left-0 w-full h-full bg-[length:400%_400%] blur animate-rainbow ${
          isListening ? 'animate-glow-pulse' : ''
        } rounded-lg`}
        style={{
          backgroundImage: 'linear-gradient(45deg, #ff0000, #ff7300, #fffb00, #48ff00, #00ffd5, #002bff, #7a00ff, #ff00c8, #ff0000)',
          top: '-2px',
          left: '-2px',
          width: 'calc(100% + 4px)',
          height: 'calc(100% + 4px)',
        }}
      />

      {/* Button */}
      <button 
        className="relative px-8 py-2 bg-neutral-900 text-white rounded-lg cursor-pointer select-none"
        role="button"
        onClick={onClick}
        disabled={isListening}
      >
        {isListening ? "Listening..." : "ID Song ðŸŽ§"}
      </button>
    </div>
  );
};
