import React from 'react';
import { SongData } from '../types/song';

interface TopSectionProps {
  songData: SongData | null;
}

export const TopSection: React.FC<TopSectionProps> = ({ songData }) => {
  if (!songData) return null;

  return (
    <div className="flex items-center mb-5">
      <div className="mr-5 flex-shrink-0">
        <img 
          src={songData.album_art || "/api/placeholder/150/150"} 
          alt={`${songData.album} album art`}
          className="w-[100px] h-[100px] rounded-lg shadow-md object-cover" 
        />
      </div>
      <div className="text-left">
        <h1 className="text-title-1 font-bold text-system-primary m-0">
          {songData.title}
        </h1>
        <p className="text-body text-system-secondary my-[5px] transition-colors duration-200">
          {songData.artist}
        </p>
        <p className="text-body text-system-secondary my-[5px] transition-colors duration-200">
          {songData.album}
        </p>
      </div>
    </div>
  );
};
