import React, { useState, useEffect } from 'react';
import AudioRecorder from './components/AudioRecorder';
import { TopSection } from './components/TopSection';
import { Lyrics } from './components/Lyrics';
import { Logo } from './components/Logo';
import { SongData } from './types/song';
import { getBrightestColor } from './utils/colorUtils';
import './styles/tailwind.css';

const App: React.FC = () => {
  const [songData, setSongData] = useState<SongData | null>(null);
  const [brightColor, setBrightColor] = useState<string>('rgb(255, 255, 255)');

  useEffect(() => {
    const updateColor = async () => {
      if (songData?.album_art) {
        try {
          const color = await getBrightestColor(songData.album_art);
          setBrightColor(color);
        } catch {
          setBrightColor('rgb(255, 255, 255)');
        }
      }
    };

    updateColor();
  }, [songData?.album_art]);

  const handleSongRecognized = (result: SongData) => {
    setSongData(result);
  };

  return (
    <div className="min-h-screen w-full relative bg-zinc-900">
      {/* Background */}
      {songData?.album_art && (
        <div className="fixed inset-0 w-full h-full">
          <div 
            className="absolute w-[200%] h-[200%] left-[-50%] top-[-50%]"
            style={{ 
              backgroundImage: `url(${songData.album_art})`,
              backgroundPosition: 'center center',
              backgroundSize: 'cover',
              transform: 'scale(0.5)',
              filter: 'blur(100px)',
            }}
          />
        </div>
      )}

      {/* Content Container */}
      <div className="relative z-10 min-h-screen flex flex-col items-center px-8 py-8">
        {!songData ? (
          // Initial Screen Layout
          <div className="flex flex-col items-center justify-center flex-1 space-y-8">
            <Logo size="large" />
            <AudioRecorder onSongRecognized={handleSongRecognized} />
            <div className="project-subtext">
              A <span className="funzzies">4Funzzies</span> Project
            </div>
          </div>
        ) : (
          // Post-Recognition Layout
          <div className="space-y-6 w-full max-w-2xl">
            <div className="flex items-center justify-between">
              <Logo size="small" />
              <AudioRecorder onSongRecognized={handleSongRecognized} />
            </div>

            {/* Song Info Card */}
            <div className="bg-black/30 backdrop-blur-xl rounded-lg p-5">
              <TopSection songData={songData} brightColor={brightColor} />
            </div>

            {/* Lyrics Card with Internal Scroll */}
            <div className="bg-black/30 backdrop-blur-xl rounded-lg p-5 h-[400px] flex flex-col">
              <div className="overflow-y-auto flex-1">
                <Lyrics songData={songData} brightColor={brightColor} />
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
