import React, { useState } from 'react';
import AudioRecorder from './components/AudioRecorder';
import { TopSection } from './components/TopSection';
import { Lyrics } from './components/Lyrics';
import { SongData } from './types/song';
import './styles/tailwind.css';

const App: React.FC = () => {
  const [songData, setSongData] = useState<SongData | null>(null);

  const handleSongRecognized = (result: SongData) => {
    setSongData(result);
  };

  return (
    <div className="m-0 font-system bg-page-bg text-system-secondary antialiased flex justify-center items-center h-screen">
      <div className="w-full max-w-[800px] px-5">
        {/* Background with album art - keeping exact same blur effect */}
        {songData?.album_art && (
          <div 
            className="fixed inset-0 bg-cover bg-center opacity-20 filter blur-xl"
            style={{ backgroundImage: `url(${songData.album_art})` }}
          />
        )}

        <div className="relative w-full">
          {/* Static Top Section */}
          <div className="mb-5">
            <AudioRecorder onSongRecognized={handleSongRecognized} />
            <TopSection songData={songData} />
          </div>

          {/* Lyrics Section with backdrop blur */}
          <div className="bg-system-toolbar backdrop-blur-60 rounded-lg p-5 max-h-[60vh] overflow-y-auto shadow-lg">
            {songData && <Lyrics songData={songData} />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
