import React, { useState } from 'react';
import Logo from './components/Logo/Logo';
import SongIdentifier from './components/SongIdentifier/SongIdentifier';
import SongInfo from './components/SongInfo/SongInfo';
import Lyrics from './components/Lyrics/Lyrics';
import { SongState } from './types/song';
import { identifySong } from './services/api';

const App: React.FC = () => {
  const [songState, setSongState] = useState<SongState>({
    isIdentifying: false,
    song: null,
    error: null
  });

  const handleIdentify = async () => {
    try {
      setSongState(prev => ({ ...prev, isIdentifying: true }));
      const song = await identifySong();
      setSongState({
        isIdentifying: false,
        song,
        error: null
      });
    } catch (err) {
      setSongState({
        isIdentifying: false,
        song: null,
        error: 'Failed to identify song'
      });
    }
  };

  return (
    <div className="min-h-screen bg-app-bg text-white p-8">
      <div className="max-w-3xl mx-auto space-y-8">
        <div className="text-center space-y-8">
          <Logo />
          <SongIdentifier 
            onIdentify={handleIdentify}
            isIdentifying={songState.isIdentifying}
          />
        </div>

        {songState.song && (
          <div className="space-y-8 animate-fade-in">
            <SongInfo song={songState.song} />
            <Lyrics lyrics={songState.song.lyrics} />
          </div>
        )}

        {songState.error && (
          <div className="text-red-400 text-center">
            {songState.error}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
