import React, { useState, useCallback } from 'react';
import SongInfo from './components/SongInfo';
import Lyrics from './components/Lyrics';
import { Song } from './types/song';
import { AudioRecorder } from './services/audioService';
import { identifySong } from './services/songService';

const App: React.FC = () => {
  const [song, setSong] = useState<Song | null>(null);
  const [lyrics, setLyrics] = useState<string[]>([]);
  const [isIdentifying, setIsIdentifying] = useState(false);
  const audioRecorder = new AudioRecorder();

  const handleIdentify = useCallback(async () => {
    try {
      setIsIdentifying(true);
      await audioRecorder.startRecording();

      // Record for 3 seconds
      await new Promise(resolve => setTimeout(resolve, 3000));

      const audioBlob = await audioRecorder.stopRecording();
      const identifiedSong = await identifySong(audioBlob);

      setSong(identifiedSong);
      if (identifiedSong.lyrics) {
        setLyrics(identifiedSong.lyrics);
      }
    } catch (error) {
      console.error('Error identifying song:', error);
    } finally {
      setIsIdentifying(false);
    }
  }, []);

  return (
    <div className="min-h-screen bg-[#1f1f1f] p-5">
      <div className="container max-w-[800px] mx-auto">
        <button
          onClick={handleIdentify}
          disabled={isIdentifying}
          className={`
            px-6 py-3 bg-gradient-to-r from-green-400 to-blue-400
            rounded-lg text-white font-semibold mb-8
            hover:shadow-lg transition-all duration-300
            ${isIdentifying ? 'opacity-50 cursor-not-allowed' : 'hover:scale-105'}
          `}
        >
          {isIdentifying ? 'Identifying...' : 'ID Song'}
        </button>

        {song && <SongInfo song={song} />}
        {lyrics.length > 0 && <Lyrics lyrics={lyrics} />}
      </div>
    </div>
  );
};

export default App;
