import React from 'react';
import { Song } from '../types/song';

interface SongInfoProps {
  song: Song | null;
}

const SongInfo: React.FC<SongInfoProps> = ({ song }) => {
  if (!song) return null;

  return (
    <div className="flex items-center mb-5">
      <div className="w-[100px] h-[100px] mr-5">
        <img 
          src="https://via.placeholder.com/150" 
          alt="Album Art"
          className="w-full h-full rounded-lg object-cover"
        />
      </div>
      <div className="flex flex-col">
        <h1 className="text-2xl font-bold text-white mb-1">{song.title}</h1>
        <p className="text-white/60">{song.artist}</p>
        <p className="text-white/60">{song.album}</p>
      </div>
    </div>
  );
};

export default SongInfo;
