import React from 'react';
import { SongData } from '../types/song';

interface LyricsProps {
  songData: SongData;
  brightColor: string;
}

export const Lyrics: React.FC<LyricsProps> = ({ songData, brightColor }) => {
  if (!songData.lyrics || songData.lyrics.length === 0) {
    return (
      <div 
        className="text-body leading-relaxed transition-colors duration-200"
        style={{ color: brightColor }}
      >
        <p>No lyrics available for this song.</p>
      </div>
    );
  }

  return (
    <div className="text-body leading-relaxed pr-2">
      {songData.lyrics.map((line, index) => (
        <p 
          key={index} 
          className="my-2 transition-colors duration-200"
          style={{ color: brightColor }}
        >
          {line}
        </p>
      ))}
    </div>
  );
};
