import React, { useState } from 'react';
import AudioRecorder from './components/AudioRecorder';
import { TopSection } from './components/TopSection';
import { Lyrics } from './components/Lyrics';
import { SongData } from './types/song';
import './styles/tailwind.css';

const App: React.FC = () => {
  const [songData, setSongData] = useState<SongData | null>(null);

  const handleSongRecognized = (result: SongData) => {
    setSongData(result);
  };

  return (
    <div className="m-0 font-[-apple-system,BlinkMacSystemFont,'Apple_Color_Emoji','SF_Pro','SF_Pro_Icons','Helvetica_Neue',Helvetica,Arial,sans-serif] bg-bg-primary text-text-secondary antialiased flex justify-center items-center h-screen">
      {/* Keep exact same container structure */}
      <div className="w-full max-w-[800px] p-5">
        {/* Background with album art - keeping exact same structure */}
        {songData?.album_art && (
          <div 
            className="absolute top-0 left-0 w-full h-full bg-cover bg-center opacity-20 filter blur-lg"
            style={{ backgroundImage: `url(${songData.album_art})` }}
          />
        )}

        <div className="relative">
          {/* Static Top Section - keeping exact same structure */}
          <div className="static-header">
            <AudioRecorder onSongRecognized={handleSongRecognized} />
            <TopSection songData={songData} />
          </div>

          {/* Scrollable Lyrics Section - keeping exact same structure */}
          <div className="bg-toolbar-bg backdrop-blur-[60px] rounded-lg p-5 max-h-[60vh] overflow-y-auto">
            {songData && <Lyrics songData={songData} />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
