import React, { useState, useEffect } from 'react';
import { TopSection } from './components/TopSection';
import { Song } from './types/song';
import { useAudioRecorder } from './hooks/useAudioRecorder';
import { identifySong } from './services/songService';
import { SongInfo } from './components/SongInfo/SongInfo';
import { Lyrics } from './components/Lyrics';

const App: React.FC = () => {
  const [song, setSong] = useState<Song | null>(null);
  const [isIdentifying, setIsIdentifying] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { startRecording, audioBlob, isRecording, reset } = useAudioRecorder();

  // Handle audio blob changes
  useEffect(() => {
    const identifySongFromBlob = async () => {
      if (audioBlob && isIdentifying) {
        try {
          const identifiedSong = await identifySong(audioBlob);
          setSong(identifiedSong);
        } catch (err) {
          console.error('Error:', err);
          setError('Failed to identify song. Please try again.');
        } finally {
          setIsIdentifying(false);
          reset();
        }
      }
    };

    identifySongFromBlob();
  }, [audioBlob, isIdentifying, reset]);

  const handleIdentify = async () => {
    try {
      setError(null);
      setSong(null);
      setIsIdentifying(true);
      await startRecording();
    } catch (err) {
      console.error('Error:', err);
      setError('Failed to access microphone. Please try again.');
      setIsIdentifying(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#1f1f1f] p-5">
      <div className="container max-w-[800px] mx-auto">
        <TopSection 
          isIdentifying={isIdentifying || isRecording}
          onIdentify={handleIdentify}
        />

        {error && (
          <div className="text-red-500 text-center mb-4">
            {error}
          </div>
        )}

        {song && (
          <div className="space-y-8 animate-fade-in">
            <SongInfo song={song} />
            {song.lyrics && song.lyrics.length > 0 && (
              <Lyrics lyrics={song.lyrics} />
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
