import React, { useState } from 'react';
import AudioRecorder from './components/AudioRecorder';
import { TopSection } from './components/TopSection';
import { Lyrics } from './components/Lyrics';
import { SongData } from './types/song';
import './styles/tailwind.css';

const App: React.FC = () => {
  const [songData, setSongData] = useState<SongData | null>(null);

  const handleSongRecognized = (result: SongData) => {
    setSongData(result);
  };

  return (
    <div className="min-h-screen w-full relative">
      {/* Blurred album art background or default dark gray */}
      <div 
        className={`absolute inset-0 transition-all duration-500 ease-in-out ${
          songData?.album_art ? 'opacity-50' : 'opacity-100'
        }`}
      >
        {songData?.album_art ? (
          <div 
            className="absolute inset-0 bg-cover bg-center blur-xl"
            style={{ 
              backgroundImage: `url(${songData.album_art})`,
              filter: 'blur(20px)',
            }}
          />
        ) : (
          <div className="absolute inset-0 bg-zinc-900" />
        )}
      </div>

      {/* Content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center p-8">
        <div className="mb-8">
          <AudioRecorder onSongRecognized={handleSongRecognized} />
        </div>

        <div className="w-full max-w-2xl">
          {songData && (
            <div className="glass-card">
              <TopSection songData={songData} />
              <div className="mt-6">
                <Lyrics songData={songData} />
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
