import React from 'react';
import { useAudioRecorder } from '../hooks/useAudioRecorder';
import { identifySong } from '../services/songService';
import { Song } from '../types/song';

interface AudioRecorderProps {
  onSongRecognized: (song: Song) => void;
}

const AudioRecorder: React.FC<AudioRecorderProps> = ({ onSongRecognized }) => {
  const { startRecording, isRecording, audioBlob, reset } = useAudioRecorder();

  const handleClick = async () => {
    if (isRecording) return;

    try {
      await startRecording();
    } catch (err) {
      console.error('Failed to start recording:', err);
    }
  };

  // Handle the audio blob when recording completes
  React.useEffect(() => {
    const identifyAudio = async () => {
      if (audioBlob && !isRecording) {
        try {
          const song = await identifySong(audioBlob);
          onSongRecognized(song);
          reset();
        } catch (err) {
          console.error('Failed to identify song:', err);
          reset();
        }
      }
    };

    identifyAudio();
  }, [audioBlob, isRecording, onSongRecognized, reset]);

  return (
    <button
      onClick={handleClick}
      disabled={isRecording}
      className={`
        relative px-6 py-2 text-white font-semibold
        rounded-lg bg-gradient-to-r from-green-400 to-blue-400
        transform hover:scale-105 transition-all duration-300
        hover:shadow-lg hover:shadow-green-400/50
        active:scale-95 disabled:opacity-50 disabled:cursor-not-allowed
        ${isRecording ? 'animate-pulse' : 'animate-glow'}
      `}
    >
      <span className="relative z-10">
        {isRecording ? 'Listening...' : 'ID Song'}
      </span>
      <div className="absolute inset-0 bg-gradient-to-r from-green-400 to-blue-400 opacity-75 blur-lg rounded-lg" />
    </button>
  );
};

export default AudioRecorder;
